INSERT INTO mrnanny_chat (author, text, topic) VALUES
('Mr. Nanny', 'To get into entry-level AI jobs, start by learning Python and basic data analysis. Free online courses are a great way to begin.', 'ai_jobs'),
('Mr. Nanny', 'Build small projects in machine learning or data science and share them on GitHub. Employers love to see real work!', 'ai_jobs'),
('Mr. Nanny', 'Networking is key. Join AI communities, attend webinars, and connect with professionals on LinkedIn.', 'ai_jobs'),
('Mr. Nanny', 'Look for internships or junior roles with titles like "AI Research Assistant", "Data Analyst", or "Machine Learning Intern".', 'ai_jobs'),
('Mr. Nanny', 'Highlight your curiosity and willingness to learn in your resume and interviews. Entry-level AI jobs value growth mindset.', 'ai_jobs');
