INSERT INTO chat_messages (author, text, avatar, topic) VALUES
('Anna', 'A classic cheese to pair with Cabernet Sauvignon is aged cheddar. For Sauvignon Blanc, try goat cheese!', '/images/operators/anna.jpeg.png', 'cheese_pairing'),
('Anna', 'France is famous for Bordeaux and Champagne, while Italy is known for Chianti and Prosecco.', '/images/operators/anna.jpeg.png', 'wine_world'),
('Anna', 'Germany produces excellent Riesling wines, and Spain is home to Tempranillo and Cava.', '/images/operators/anna.jpeg.png', 'wine_world'),
('Anna', 'For beer lovers, Belgium offers Trappist ales, Germany has lagers and wheat beers, and Japan is known for crisp pilsners.', '/images/operators/anna.jpeg.png', 'beer_world'),
('Anna', 'Pairing beer with food? Try a stout with chocolate desserts or a pale ale with spicy dishes.', '/images/operators/anna.jpeg.png', 'beer_pairing');
INSERT INTO chat_messages (author, text, avatar, topic) VALUES
('Anna', 'For an Italian dinner, try making risotto: sauté onions, add Arborio rice, slowly stir in broth, and finish with Parmesan.', '/images/operators/anna.jpeg.png', 'italian'),
('Anna', 'A classic Mexican meal is chicken enchiladas: fill tortillas with chicken, top with salsa and cheese, then bake.', '/images/operators/anna.jpeg.png', 'mexican'),
('Anna', 'Japanese miso soup is simple: simmer dashi broth, add tofu and seaweed, then stir in miso paste.', '/images/operators/anna.jpeg.png', 'japanese'),
('Anna', 'Indian vegetable curry: sauté onions and garlic, add spices, tomatoes, and mixed veggies, then simmer until tender.', '/images/operators/anna.jpeg.png', 'indian'),
('Anna', 'French ratatouille: layer eggplant, zucchini, and tomatoes, season, and bake until soft and flavorful.', '/images/operators/anna.jpeg.png', 'french');
INSERT INTO chat_messages (author, text, avatar, topic) VALUES
('Anna', 'How about a quick veggie stir-fry tonight? It’s healthy, easy, and delicious!', '/images/operators/anna.jpeg.png', 'meal_ideas'),
('Anna', 'Try my favorite pasta recipe: cook spaghetti, toss with olive oil, garlic, and fresh tomatoes.', '/images/operators/anna.jpeg.png', 'recipes'),
('Anna', 'Planning meals ahead can save time and money. Make a simple list before you shop!', '/images/operators/anna.jpeg.png', 'meal_planning'),
('Anna', 'Store leftovers in airtight containers and label them with the date for easy tracking.', '/images/operators/anna.jpeg.png', 'food_storage'),
('Anna', 'Eating on a budget? Beans, rice, and frozen veggies are affordable and nutritious.', '/images/operators/anna.jpeg.png', 'budget'),
('Anna', 'Family meals are a great way to connect. Try making tacos together for a fun dinner!', '/images/operators/anna.jpeg.png', 'family_meals'),
('Anna', 'Did you know sushi originated in Southeast Asia before becoming popular in Japan?', '/images/operators/anna.jpeg.png', 'food_facts'),
('Anna', 'If you’re a picky eater, start with small changes—add one new veggie to your plate.', '/images/operators/anna.jpeg.png', 'picky_eaters'),
('Anna', 'Remember, food is about nourishment and joy. Be kind to yourself and enjoy every bite!', '/images/operators/anna.jpeg.png', 'encouragement');
