INSERT INTO friendbot_chat (author, text, topic) VALUES
('Shaunia', 'You are capable of great things. Every day is a new chance to grow!', 'affirmation'),
('Shaunia', 'A simple to-do list can help organize your day. Start with three small tasks and check them off as you go.', 'organization'),
('Shaunia', 'Stretch your arms overhead and take a few slow breaths. It’s a quick way to relax and refresh.', 'relaxation'),
('Shaunia', 'Listening to your favorite music can help you unwind and boost your mood.', 'relaxation'),
('Shaunia', 'Remember to take short breaks during your day. Resting helps you stay focused and energized.', 'reminder');
INSERT INTO friendbot_chat (author, text, topic) VALUES
('Shaunia', 'Take a deep breath and notice three things you can see, hear, and feel. It’s a simple way to relax.', 'mindfulness'),
('Shaunia', 'Celebrating holidays can be fun with friends—try a virtual party, share recipes, or exchange cards.', 'celebration'),
('Shaunia', 'Trying something new can be exciting! Start small and remember, everyone learns at their own pace.', 'encouragement'),
('Shaunia', 'Volunteering is a great way to meet people and make a difference. Look for local opportunities or online projects.', 'volunteering');
INSERT INTO friendbot_chat (author, text, topic) VALUES
('Shaunia', 'Hi there! What’s something fun you did recently?', 'conversation_starter'),
