INSERT INTO relocationbot_chat (author, text, topic) VALUES
('Relocation Bot', 'Each city has its own vibe—some are busy and fast-paced, others are quiet and laid-back. Research what fits your lifestyle.', 'cities'),
('Relocation Bot', 'States often have different climates, cultures, and laws. Check state websites for info on taxes, schools, and local services.', 'states'),
('Relocation Bot', 'Major cities like New York, Los Angeles, and Chicago offer diverse neighborhoods and endless activities.', 'cities'),
('Relocation Bot', 'Smaller cities and towns can offer a close-knit community and a slower pace of life.', 'cities');
INSERT INTO relocationbot_chat (author, text, topic) VALUES
('Relocation Bot', 'Use apartment search websites like Zillow, Apartments.com, or local listings to find a place that fits your needs.', 'apartments'),
('Relocation Bot', 'Ask your landlord or neighbors for recommendations on setting up utilities like water, gas, and internet.', 'utilities'),
('Relocation Bot', 'Explore local eateries by checking reviews on Yelp or Google Maps, or ask locals for their favorite spots.', 'eateries');
INSERT INTO relocationbot_chat (author, text, topic) VALUES
('Relocation Bot', 'Use energy-efficient LED bulbs and unplug devices when not in use to save electricity.', 'eco_friendly'),
('Relocation Bot', 'Recycle paper, plastic, and glass. Many cities offer curbside recycling programs.', 'eco_friendly'),
('Relocation Bot', 'Composting food scraps reduces waste and helps your garden thrive.', 'eco_friendly'),
('Relocation Bot', 'Shop at local farmers markets to support small businesses and reduce your carbon footprint.', 'eco_friendly');
INSERT INTO relocationbot_chat (author, text, topic) VALUES
('Relocation Bot', 'Starting a garden in your new home? Begin with easy plants like herbs, tomatoes, or marigolds.', 'gardening'),
('Relocation Bot', 'Check your local climate and soil type to choose the best plants for your area.', 'gardening'),
('Relocation Bot', 'Joining a community garden is a great way to meet neighbors and learn gardening tips.', 'gardening'),
('Relocation Bot', 'Remember to water your plants regularly and give them plenty of sunlight.', 'gardening');
INSERT INTO relocationbot_chat (author, text, topic) VALUES
('Relocation Bot', 'Start planning your move early. Make a checklist for packing, utilities, and important documents.', 'planning'),
('Relocation Bot', 'Research neighborhoods online and visit if possible before choosing a new home.', 'housing'),
('Relocation Bot', 'Set up utilities like electricity, water, and internet ahead of time to avoid delays.', 'utilities'),
('Relocation Bot', 'Public transportation, local taxis, and rideshare apps can help you get around your new city.', 'transportation'),
('Relocation Bot', 'Explore your new city by visiting parks, museums, and local restaurants.', 'city_guide'),
('Relocation Bot', 'Join community groups or clubs to meet new people and make friends.', 'community'),
('Relocation Bot', 'Moving with pets? Make sure they have updated tags and a comfortable travel carrier.', 'pets'),
('Relocation Bot', 'On moving day, keep essentials like snacks, chargers, and important papers handy.', 'moving_day'),
('Relocation Bot', 'It’s normal to feel a little out of place at first. Give yourself time to adjust and explore.', 'adjustment'),
('Relocation Bot', 'Fun fact: Tokyo is the world’s largest city by population, while Reykjavik is one of the smallest capitals.', 'city_facts'),
('Relocation Bot', 'Find relocation resources and support at sites like Moving.com and Expatica.com.', 'resources');
